mapper.select:
select * from t_mem_module


    and module_code = #{moduleCode,jdbcType=VARCHAR}


    and parent_id = #{parentId,jdbcType=BIGINT}


    and name = #{name,jdbcType=VARCHAR}


    and leaf_level = #{leafLevel,jdbcType=INTEGER}


    and permission = #{permission,jdbcType=VARCHAR}


    and curl = #{curl,jdbcType=VARCHAR}


    and type = #{type,jdbcType=INTEGER}


    and status = #{status,jdbcType=INTEGER}


    and belong = #{belong,jdbcType=VARCHAR}


    and remark = #{remark,jdbcType=VARCHAR}


    and order_no = #{orderNo,jdbcType=INTEGER}


    and is_deleted = #{isDeleted,jdbcType=INTEGER}


    and create_by = #{createBy,jdbcType=BIGINT}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_by = #{updateBy,jdbcType=BIGINT}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}
